INSERT INTO `mn_catimages` (`Id`, `title_vn`, `title_en`, `description_vn`, `description_en`, `images`, `parentid`, `sort`, `ticlock`, `alias`, `meta_title`, `meta_keyword`, `meta_description`, `home`) VALUES
(1, 'Bộ Ảnh Áo Dài Tại Lăng Ông Bà Chiểu', 'Ministry Photo Ao Dai at Tomb of Mr. Ba Chieu', '', '', 'bo-anh-ao-dai-tai-lang-ong-ba-chieu-wywaotobg0.jpg', NULL, 1, '0', 'bo-anh-ao-dai-tai-lang-ong-ba-chieu', 'Bộ Ảnh Áo Dài Tại Lăng Ông Bà Chiểu', 'nhiếp ảnh, áo dài, bộ ảnh áo dài, lăng ông bà chiểu, chụp ảnh áo dài', 'bộ ảnh áo dài trắng thướt tha được chụp tại lăng ông Bà Chiểu tôn lên vẻ đẹp dịu dàng của người con gái Việt Nam', NULL),
(2, 'Bộ Ảnh Sài Gòn Sau Vai Của Một Nhóm Bạn Trẻ', 'Ministry Photo Saigon After Shoulder Of a group of young', '', '', 'bo-anh-sai-gon-sau-vai-cua-mot-nhom-ban-tre-hqnxiqinzo.jpg', NULL, 2, '0', 'bo-anh-sai-gon-sau-vai-cua-mot-nhom-ban-tre', 'Bộ Ảnh Sài Gòn Sau Vai Của Một Nhóm Bạn Trẻ', 'cuộc sống sài gòn, sài gòn chuyện đời của phố, người sài gòn, sài gòn trong tôi, đường phố sài gòn', 'Bộ ảnh Sài Gòn sau vai của một nhóm bạn trẻ yêu mến Sài Gòn, họ đã thể hiện tình cảm đó thành những tấm ảnh rất dễ thương và sinh động', NULL),
(3, 'Bộ Tranh Sài Gòn Đáng Yêu Của Nhóm Kín Illustration', 'Ministry Saigon Painting Tight Group lovely illustration', '', '', 'bo-tranh-sai-gon-thu-nho-dang-yeu-cua-nhom-kin-illustration-wshuuicdme.jpg', NULL, 3, '0', 'bo-tranh-sai-gon-thu-nho-dang-yeu-cua-nhom-kin-illustration', 'Những Bức Tranh Đáng Yêu Về Sài Gòn', 'sài gòn, tranh sài gòn, đường phố sài gòn, ảnh đẹp sài gòn, sài gòn giản dị', 'Bộ tranh đáng yêu về những căn nhà khu phố giản dị và quen thuộc của Sài Gòn. Nó làm cho giới trẻ cảm thấy thú vị về một thành phố vừa hiện đại vừa gần gũi', NULL),
(4, 'Bộ Ảnh Chụp Tại Bàu Trắng Phan Thiết', 'Ministry Photo Shoot At Bau Trang Phan Thiet', '', '', 'bo-anh-chup-tai-bau-trang-phan-thiet-00ztksvxdw.jpg', NULL, 4, '0', 'bo-anh-chup-tai-bau-trang-phan-thiet', 'Bộ Ảnh Chụp Tại Bàu Trắng Phan Thiết', 'địa điểm du lịch, du lịch phan thiết, địa điểm chụp hình, bàu trắng, du lịch biển', 'Bàu Trắng từ lâu đã là một địa điểm du lịch lý tưởng. Nằm cách không xa thành phố Phan Thiết, con đường đi đến bàu trắng dài tăm tắp ôm ngang biển với những đồi cát tuyệt đẹp', NULL),
(5, 'Nhà Thờ Chánh Tòa Phú Cường', 'Phu Cuong Cathedral', '', '', 'nha-tho-chanh-toa-phu-cuong-m5dlrjnfhn.jpg', NULL, 5, '0', 'nha-tho-chanh-toa-phu-cuong', 'Những Nhà Thờ Đẹp Của Việt Nam', 'nhà thờ, nhà thờ thiên chúa giáo, nhà thờ chánh tòa, công giáo việt nam, chụp hình nhà thờ', 'Là một nhà thờ đẹp, chánh tòa Phú Cường nằm trên một ngọn đồi có thể nhìn thấy từ xa, nhà thờ có kiến trúc tây phương, là nơi để chụp ảnh cưới rất đẹp', NULL),
(6, 'Bộ Ảnh Thiền Viện Trúc Lâm Đà Lạt', 'Ministry Photo Truc Lam Dalat', '', '', 'bo-anh-thien-vien-truc-lam-da-lat-lyiz6mrlna.jpg', NULL, 6, '0', 'bo-anh-thien-vien-truc-lam-da-lat', 'Bộ Ảnh Vườn Hoa Thiền Viện Trúc Lâm Đà Lạt', 'đà lạt, vườn hoa, thiền viện trúc lâm, bộ ảnh đà lạt, ảnh đà lạt', 'nằm trong khuôn viên hồ Tuyền Lâm của Tp Đà Lạt, thiền viện Trúc Lâm có khung cảnh bình yên. Vườn hoa của thiền viện rực rỡ với nhiều loài hoa đẹp', NULL),
(7, 'Một Chút Hương Quê', 'Grass pasture', '', '', 'mot-chut-huong-que-lggdym6hpu.jpg', NULL, 7, '0', 'mot-chut-huong-que', 'Mùi Hương Lúa Non Quê Nhà', 'nhiếp ảnh, ảnh chụp thiên nhiên, phong cảnh, đồng quê, cánh đồng lúa', 'Những cánh đồng lúa mênh mông ở vùng quê miền Bắc luôn cho tôi cảm giác bồi hồi kỷ niệm cho dù tuổi thơ tôi không phải lớn lên ở đây', NULL);

