CREATE TABLE IF NOT EXISTS `mn_product` (
  `Id` int(11) NOT NULL AUTO_INCREMENT,
  `title_vn` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `description_vn` text COLLATE utf8_unicode_ci NOT NULL,
  `content_vn` longtext COLLATE utf8_unicode_ci,
  `idcat` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `idmanufacturer` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `images` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `price` varchar(200) COLLATE utf8_unicode_ci NOT NULL DEFAULT '0',
  `codepro` char(100) COLLATE utf8_unicode_ci NOT NULL,
  `sort` int(11) NOT NULL DEFAULT '0',
  `ticlock` enum('0','1') COLLATE utf8_unicode_ci DEFAULT '0',
  `hot` tinyint(1) DEFAULT '0',
  `alias` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `sale_price` int(11) NOT NULL DEFAULT '0',
  `date` int(11) DEFAULT NULL,
  `meta_keyword` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `meta_description` varchar(255) COLLATE utf8_unicode_ci DEFAULT NULL,
  `view` int(11) DEFAULT '0',
  `home` tinyint(1) DEFAULT '0',
  `title_en` varchar(500) CHARACTER SET utf8 DEFAULT NULL,
  `description_en` text CHARACTER SET utf8,
  `content_en` text COLLATE utf8_unicode_ci,
  PRIMARY KEY (`Id`),
  FULLTEXT KEY `title_vn` (`title_vn`,`content_vn`),
  FULLTEXT KEY `title_vn_2` (`title_vn`),
  FULLTEXT KEY `title_vn_3` (`title_vn`),
  FULLTEXT KEY `title_vn_4` (`title_vn`,`content_vn`),
  FULLTEXT KEY `title_vn_5` (`title_vn`,`content_vn`),
  FULLTEXT KEY `title_vn_6` (`title_vn`,`content_vn`)
) ENGINE=MyISAM  DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci AUTO_INCREMENT=8 ;

