CREATE TABLE IF NOT EXISTS `mn_weblink` (
  `Id` int(11) NOT NULL AUTO_INCREMENT,
  `title_vn` varchar(255) CHARACTER SET utf8 COLLATE utf8_unicode_ci DEFAULT NULL,
  `title_en` varchar(255) NOT NULL,
  `description_vn` text CHARACTER SET utf8,
  `description_en` text CHARACTER SET utf8,
  `images` varchar(200) NOT NULL,
  `content_vn` text CHARACTER SET utf8,
  `content_en` text CHARACTER SET utf8,
  `sort` int(11) NOT NULL,
  `ticlock` enum('0','1') NOT NULL,
  `view` int(15) DEFAULT '0',
  `hot` int(1) DEFAULT '0',
  `location` varchar(255) CHARACTER SET utf8 DEFAULT NULL,
  `link_view` varchar(255) CHARACTER SET utf8 DEFAULT NULL,
  `giangvien` varchar(255) CHARACTER SET utf8 DEFAULT NULL,
  `link` varchar(255) CHARACTER SET utf8 DEFAULT NULL,
  `date` int(11) DEFAULT NULL,
  `alias` varchar(255) CHARACTER SET utf8 DEFAULT NULL,
  `meta_description` text CHARACTER SET utf8,
  `meta_keyword` varchar(255) CHARACTER SET utf8 DEFAULT NULL,
  PRIMARY KEY (`Id`)
) ENGINE=MyISAM  DEFAULT CHARSET=latin1 AUTO_INCREMENT=2 ;

